const foo = new grafana.oss.DataSource("foo", {
    type: "cloudwatch",
    name: "cw-example",
    jsonDataEncoded: JSON.stringify({
        defaultRegion: "us-east-1",
        authType: "keys",
    }),
    secureJsonDataEncoded: JSON.stringify({
        accessKey: "123",
        secretKey: "456",
    }),
});
