    datasourceUid: foo.uid,
    permissions: [
        {
            teamId: team.id,
            permission: "Edit",
        },
        {
            userId: user.id,
            permission: "Edit",
        },
        {
            builtInRole: "Viewer",
            permission: "Query",
        },
        {
            userId: sa.id,
            permission: "Query",
        },
    ],
});
