    name: "terraform_test_sa",
    role: "Viewer",
});
const test = new grafana.enterprise.RoleAssignment("test", {
    roleUid: testRole.uid,
    users: [testUser.id],
    teams: [testTeam.id],
    serviceAccounts: [testSa.id],
});
