    probes=[main.probes["atlanta"]],
    labels={
        "foo": "bar",
    },
    settings=grafana.synthetic_monitoring.CheckSettingsArgs(
        dns=grafana.synthetic_monitoring.CheckSettingsDnsArgs(),
    ))
