    name: sm_metrics_publish
    properties:
      region: ${cloudRegion}
      accessPolicyId: ${smMetricsPublish.policyId}
      name: metric-publisher-for-sm
  smStackInstallation:
    type: grafana:syntheticMonitoring:Installation
    name: sm_stack
    properties:
      stackId: ${smStack.id}
      metricsPublisherKey: ${smMetricsPublishAccessPolicyToken.token}
