    metadata: {
        uid: "test_enrichment",
    },
    spec: {
        title: "Comprehensive alert enrichment",
        description: "Demonstrates many enrichment steps and configurations",
        alertRuleUids: [
            "alert-rule-1",
            "alert-rule-2",
        ],
        receivers: [
            "webhook",
            "slack-critical",
        ],
        labelMatchers: [
            {
                type: "=",
                name: "severity",
                value: "critical",
            },
            {
                type: "=~",
                name: "team",
                value: "alerting|alerting-team",
            },
        ],
        annotationMatchers: [{
            type: "!=",
            name: "runbook_url",
            value: "",
        }],
        steps: [
            {
                assign: {
                    timeout: "30s",
                    annotations: {
                        priority: "high",
                        runbook_url: "https://runbooks.grafana.com/alert-handling",
                    },
                },
            },
            {
                external: {
                    url: "https://some-api.grafana.com/alert-enrichment",
                },
            },
            {
                dataSource: {
                    timeout: "30s",
                    logsQuery: {
                        dataSourceType: "loki",
                        dataSourceUid: "loki-uid-123",
                        expr: "{job=\"my-app\"} |= \"error\"",
                        maxLines: 5,
                    },
                },
            },
            {
                dataSource: {
                    timeout: "30s",
                    rawQuery: {
                        refId: "A",
                        request: JSON.stringify({
                            datasource: {
                                type: "prometheus",
                                uid: "prometheus-uid-456",
                            },
                            expr: "rate(http_requests_total[5m])",
                            refId: "A",
                            intervalMs: 1000,
                            maxDataPoints: 43200,
                        }),
                    },
                },
            },
            {
                sift: {},
            },
            {
                explain: {
                    annotation: "ai_explanation",
                },
            },
            {
                assistantInvestigations: {},
            },
            {
                conditional: {
                    "if": {
                        labelMatchers: [{
                            type: "=",
                            name: "severity",
                            value: "critical",
                        }],
                    },
                    then: {
                        steps: [
                            {
                                assign: {
